#!/bin/bash
#support multiple args
for arg in "$@" 
do
  #strip ending slash if present (tab-completion adds them)
  arg=${arg%/}
  #compress with pigz (faster on multicore systems)
  tar c "$arg" | pigz - > "$arg".tgz 
done

