killpid() {
     PID=`ps -aux | grep "commandnamehere" | grep -v "grep" | awk '{print $2}'`;
     if [ ! -z "$PID" ]
     then
         echo "killing commandnamehere - $PID";
         echo $PID | xargs kill;
     fi
 }
