so_42118992: for i in $(pgrep -f "server.php"); do
    pidtime=$(stat -c '%Y' /proc/$i)
    now=$(date +%s)
    ((now - pidtime >= 6)) && {
      kill $i;
      sleep 1;
      kill &>/dev/null -9 $i;
    }
done

