pbpaste |awk 'BEGIN{ close(cmd); }
{ cmd="pwgen 10 1";
  if (cmd | getline pass) {
      print substr($0, 1, length($0) -1) ">>myfile-" pass ".jpg";
      close(cmd);
  } else { print "running command failed!"; exit 1; }
}'

pbpaste |awk 'BEGIN{ close(cmd); }
{ cmd="pwgen 10 1 || echo failed";
