so_34763457: # Call with year month day
# e.g. convert_date_to_epoch 2015 01 01
function convert_date_to_epoch() {
    awk -v year=${1} -v month=${2} -v day=${3} 'BEGIN {
        agg_date = year " " month " " day " 00 00 00";
        utime = mktime(agg_date);
        print utime;
    }'
}

