so_17280714: gps | % {
  $line = ($_|out-string).substring(244,80)
  if ($_.ws -lt 100MB) {
    write-host $line
  } elseif ($_.ws -lt 150MB) {
    write-host $line -foregroundcolor yellow
  } else {
    write-host $line -foregroundcolor red
  }
}
