    tmp=$(mktemp -dt "${file}")
    < ${file} wc -l | cat - ${file} > ${tmp}
    mv ${tmp} ${file}
done

function AddLineCount {
    tmp=$(mktemp -dt "$1")
    < $1  wc -l | cat - $1 > ${tmp}
    mv ${tmp} $1
}

# With a parallelism of 10
